import {
    makeObservable,
    observable,
    action,
    computed,
    when,
    reaction,
    autorun
  } from "mobx";
//   import shortid from "shortid";
  
  class Store {
    tasks = [];
    constructor() {
      makeObservable(this, {
        tasks: observable,
        totalTask: computed,
        addNewTask: action.bound,
        removeTask: action.bound
      });
  
      autorun(() => console.log("run each state change", this.tasks.slice()));
  
      reaction(
        () => this.totalTask,
        (totalTask) => {
          console.log(totalTask);
        }
      );
  
      when(
        () => this.totalTask >= 3,
        () => (this.tasks = [])
      );
    }
  
    get totalTask() {
      return this.tasks.length;
    }
  
    addNewTask(task) {
      this.tasks.push({ id: 1, value: task });
    }
  
    removeTask(id) {
      this.tasks = this.tasks.filter((task) => task.id !== id);
    }
  }
  
  const store = new Store();
  
  export default store;
