import React from 'react';
import PropTypes from 'prop-types';
import { Button, Grid, InputAdornment, MenuItem, Typography, makeStyles, Paper, OutlinedInput, FormControl, IconButton, Select } from '@material-ui/core';
// import logo from '../../assets/logo.png';
const useStyles = makeStyles((theme) => ({
    backgroundLogin: {
        //   backgroundImage: `url(${backgroundLogin})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: '100%'
    },
    paperForm: {
        paddingRight: '15px',
        paddingLeft: '15px',
        marginRight: 'auto',
        marginLeft: 'auto',
        marginTop: '15px',
        marginTop: 'auto',
        marginBottom: '15px',
        marginBottom: 'auto'
    },
    sizePaper: {
        width: '495px',
        height: '597px',
        padding: '50px 80px 50px 80px',
        margin: '100px auto',
    },
    input: {
        borderRadius: '48px',
        width: '495px',
        background: '#F7FAFB',
    },
    img: {
        width: '208px ',
        height: '52px',
        marginLeft: '50px'

    },
    title: {
        fontWeight: '600',
        fontSize: '24px',
        lineHeight: '33px',
        padding: '15px 0 20px 0',
        //   color:blackColorTitle
    },
    label: {
        //   color:blackColorTitle,
        fontWeight: '700',
        paddingBottom: '10px'
    },
    button: {
        //   background:primaryButtonColor,
        borderRadius: '40px',
        color: '#fff',
        height: '48px'
    },
    IconButton: {

    }
}))
const names = [
    'Oliver Hansen',
    'Van Henry',
    'April Tucker',
    'Ralph Hubbard',
    'Omar Alexander',
    'Carlos Abbott',
    'Miriam Wagner',
    'Bradley Wilkerson',
    'Virginia Andrews',
    'Kelly Snyder',
];
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};
function getStyles(name, personName, theme) {
    return {
      fontWeight:
        personName.indexOf(name) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }
  
const LoginPage = () => {
    const classes = useStyles();
    const [personName, setPersonName] = React.useState([]);

    const handleChange = (event) => {
      const {
        target: { value },
      } = event;
      setPersonName(
        // On autofill we get a stringified value.
        typeof value === 'string' ? value.split(',') : value,
      );
    };
    return (
        <section className={classes.backgroundLogin}>
            <div className={classes.paperForm}  >
                <Paper className={classes.sizePaper} >
                    <div>
                        {/* <img
                className={classes.img}
                src={logo}
                alt="logo"
              /> */}
                    </div>
                    <br />
                    <Typography className={classes.title}>Đăng Nhập</Typography>
                    <form
                    // onSubmit={handleSubmit(onSubmit)}
                    >
                        <Grid container spacing={4}>
                            <Grid item md={12} xs={12}>
                                <FormControl sx={{ m: 1, width: '25ch' }} variant="outlined">
                                    <Typography className={classes.label}>
                                        Điện thoại
                                    </Typography>
                                    <OutlinedInput
                                        // {...register('phone')}

                                        className={classes.input}
                                        placeholder="Điện thoại"
                                    />

                                </FormControl>
                            </Grid>
                            <Grid item md={12} xs={12}>
                                <FormControl variant="outlined">
                                    <Typography className={classes.label}>
                                        Mật khẩu
                                    </Typography>
                                    <OutlinedInput

                                        //   {...register('password')}

                                        className={classes.input}
                                        placeholder="Mật khẩu"
                                        //   type={values.showPassword ? 'text' : 'password'}
                                        endAdornment={
                                            <InputAdornment position="end">
                                                <IconButton
                                                    aria-label="toggle password visibility"
                                                    // onClick={handleClickShowPassword}
                                                    // onMouseDown={handleMouseDownPassword}
                                                    edge="end"
                                                    className={classes.IconButton}
                                                >
                                                    {/* {values.showPassword ? <Visibility />  :<VisibilityOff /> } */}
                                                </IconButton>
                                            </InputAdornment>

                                        }

                                    />

                                </FormControl>
                            </Grid>
                            <Grid item md={12} xs={12}>
                                <FormControl sx={{ m: 1, width: 300, mt: 3 }}>
                                <Typography className={classes.label}>
                                      Công ty
                                    </Typography>
                                    <Select
                                        multiple
                                        displayEmpty
                                        value={personName}
                                        onChange={handleChange}
                                        input={<OutlinedInput />}
                                        className={classes.input}
                                        renderValue={(selected) => {
                                            if (selected.length === 0) {
                                                return <em>Placeholder</em>;
                                            }

                                            return selected.join(', ');
                                        }}
                                        // MenuProps={MenuProps}
                                        inputProps={{ 'aria-label': 'Without label' }}
                                    >
                                        <MenuItem disabled value="">
                                            <em>Placeholder</em>
                                        </MenuItem>
                                        {names.map((name) => (
                                            <MenuItem
                                                key={name}
                                                value={name}
                                                style={getStyles(name, personName, theme)}
                                            >
                                                {name}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Grid>
                            <Grid item md={12} xs={12}>
                                <Typography >
                                    <a style={{
                                        cursor: 'pointer',
                                        textDecoration: 'none',
                                        color: '#0b6aaf'
                                    }}
                                    //   onClick={() => history.push('/reset-password')}
                                    >Quên mật khẩu?
                                    </a>
                                </Typography>
                            </Grid>
                            <Grid item md={12} xs={12}>
                                <Button type="submit" fullWidth variant="contained" className={classes.button}>ĐĂNG NHẬP</Button>
                            </Grid>
                        </Grid>


                    </form>
                </Paper>

            </div>
        </section>
    );
};


LoginPage.propTypes = {

};


export default LoginPage;
