import { makeObservable, observable, runInAction } from "mobx"
import * as services from './service';

export default function createCompanyStore() {
  const store = observable({ company: [] });
  const getCompany = (username) => {
    return async () => {
      try {
        const result = await services.companyList(username)
      
        runInAction(() => {
          this.companyStore.company = result.data;
        });
      } catch (err) {
      }
    }
  };
  return {
    store,
    controller: {
      getCompany,
    }
  }
}
